sp_widget.client_script:
function($scope) {

	var g_form = $scope.page.g_form;
	var field = $scope.page.field;

	// See all the methods available
	console.log(g_form);

	// Set read only of all fields
	$scope.setReadOnlyAll = function(bool) {
		console.log('setting read only');
		var fields = g_form.getFieldNames();
		for (var i in fields) {
			g_form.setReadOnly(fields[i],bool);
		}
	}

	// Toggle the visibility of a field
	$scope.toggleVisible = function(field) {
		var isVisible = g_form.isVisible(field);
		g_form.setVisible(field,!isVisible);
	}

	// set field as mandatory
	$scope.setMandatory = function(field,bool) {
		g_form.setMandatory(field,bool);
	}

	// alert with field label and value
	$scope.getValue = function(field) {
		var field_obj = g_form.getField(field);
		var value = g_form.getValue(field);
		alert(field_obj.label + ": " + value);
	}

}
